import React from 'react';
import { View,Text, Dimensions,StyleSheet, Image,TouchableOpacity, Button } from 'react-native';
import { color } from 'react-native-reanimated';
import {LinearGradient} from 'expo-linear-gradient';

import { MaterialIcons } from '@expo/vector-icons'; 


const splash = ()=>{
    return (
        <View style={styles.container}>
            <View style={styles.header}>
                <Image source={require('./../assets/sales.png')} style={styles.logo}
                resizeMode="stretch"  />
            </View>
            <View style={styles.footer}>
                <Text style = {styles.title}>WELCOME TO YOUR COMPAGNY! </Text>
                <Text style= {styles.text}>This application made to consult the sales of your company. </Text>
                {/* <Text style= {styles.text}>Created By: Ahmed ben hamouda & Aymen Jlassi</Text> */}

                <View style={styles.button}>
                       <TouchableOpacity onPress={()=>alert('Click')}>
                                <LinearGradient colors={['#E8910E', '#EB6E12']} style={styles.signIn} >
                                        <Text style={styles.textSign}>Get Started
                                        <MaterialIcons name="navigate-next" size={24} color="black" />
                                        </Text>
                                </LinearGradient>
                        </TouchableOpacity>
                
                </View>
             

            </View>
        </View>
    )
}
export default splash;

const {height} = Dimensions.get("screen");
const height_logo = height * 0.28;

const styles = StyleSheet.create({
    container:{
        flex: 1,
        backgroundColor:'#e8910e'
    },
    header:{
        flex:2,
        justifyContent:'center',
        alignItems:'center'
    },
    footer:{
        flex:1,
        backgroundColor:'#fff',
        borderTopLeftRadius:30,
        borderTopRightRadius:30,
        paddingVertical:50,
        paddingHorizontal:30,
    },
    logo:{
        width:height_logo,
        height:height_logo
    },
    title:{
        color:'#e8910e',
        fontSize:30,
        fontWeight: 'bold'
    },
    text:{
        color:'grey',
        marginTop:5
    },
    button:{
        alignItems:'flex-end',
        marginTop:30,
    },
    signIn:{
        width:150,
        height:40,
        justifyContent:'center',
        alignItems:'center',
        borderRadius:50,
        flexDirection:'row'
    },
    textSign:{
        color:'white',
        fontWeight:'bold'
    }
})
