import { NavigationContainer } from '@react-navigation/native';
import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View,TextInput } from 'react-native';
import * as Animatable from 'react-native-animatable';
import { AntDesign, FontAwesome  } from '@expo/vector-icons'; 


export default function LoginScreen() {
  return (
    <Animatable.View animation="fadeInDown" style={styles.container}>
        <View style={styles.space} >
            <Text style={styles.textHeadre}>Welcome!</Text>    
        </View>


        <View style={styles.content}>
            <Text style={styles.title}>LOGIN</Text>
                    <View>
                        <AntDesign name="user" size={20} color="black" style={styles.PlaceIcon} />
                    {/* <FontAwesome name="user-o" size={20}/> */}
                    <TextInput placeholder="Enter your Email" style={styles.textInput}/>
                    </View>
           
                    <View>
                        <AntDesign name="lock" size={24} color="black" />
                        <TextInput placeholder="Enter your Email" style={styles.textInput}/>
                    </View>
            
        </View>
    </Animatable.View>
  );
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:'#CD4C51'
    },
    space:{
        flex:1,
        justifyContent: 'flex-end',
        paddingHorizontal: 20,
        paddingBottom: 50
    
    },
    content:{
        flex:2,
        backgroundColor:'#fff',
        borderTopRightRadius:30,
        borderTopLeftRadius:30,
        padding:30,
        
        
    },
    title:{
        fontSize:30,
        color:'#CD4C51',
        fontFamily:'monospace',
        textAlign:'center',
        marginBottom:55,
        fontWeight:'bold',
    },
    textInput:{
        color:'#05375a',
        borderBottomWidth:1,
        marginBottom:40,
        paddingLeft:26,
        paddingBottom:4
        
    },
    textHeadre:{
        color:'#fff',
        fontWeight:'bold',
       
        fontSize:26,
     
    },
    PlaceIcon:{
        position:'absolute',
        marginBottom:2
    }
})
